// Logo
// Page control
// Wallet connection

/** 
 * Design:The header must be transparent so that the background can be seen through.
 * 
 * */ 
import logo from '../logo.svg';
import styled from 'styled-components'
// import dummyClick from '../debug/debug.jsx'
import React, { Component } from 'react'

const Img = styled.img`
  height: 4rem;
  pointer-events: none;

  @media (prefers-reduced-motion: no-preference) {
    animation: App-logo-spin infinite 20s linear;
  }

  @keyframes App-logo-spin {
    from {
      transform: rotate(0deg);
    }
    to {
      transform: rotate(360deg);
    }
  }
`

const Header = styled.header`
  min-height: 10vh;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
  margin: 0px 10px 0px 10px
`

const LogoTitle = styled.div`
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: flex-start;
    color: white;
`

const H1 = styled.h1`
  font-size: 2rem;
  font-weight: bold;
`

const PageControl = styled.div`
    color: black
`

const Button = styled.button`
  width: 200px
`
const NavigationButton = styled.button`
    color: #ffffff;
    font-weight: bold;
    background-color: #000;
    border-color: #303030;
    opacity: 60%;
`

const aDummyClick = async (e) => {
    console.log(e)
}

const dummyClick = (e) => {
    console.log(e);
}

export default class AppHeader extends Component {
    render() {

        // Changes based on the wallet connection state
        const btnWalletTitle = 'Connect Wallet'

        return (
            <Header>
                <LogoTitle>
                    <Img src={logo} alt="logo" />
                    <H1>
                        {this.props.title}
                    </H1>
                </LogoTitle>
                <PageControl className="btn-group" role="group">
                    <NavigationButton type="button" class="btn">Trade</NavigationButton>
                    <NavigationButton type="button" class="btn">Charts</NavigationButton>
                    <NavigationButton type="button" class="btn">Orders</NavigationButton>
                </PageControl>
                <Button 
                    className='btn btn-primary' 
                    onClick={async (e) => {aDummyClick(e)}}>
                        {btnWalletTitle}
                </Button>
            </Header>
        )
    }
}
