import './App.css'
import './components/AppHeader'

import React, {useState, useEffect} from 'react'

import {HashRouter as Router} from 'react-router-dom'

import styled from 'styled-components'
import axios from 'axios'

import 'bootswatch/dist/darkly/bootstrap.min.css'
import '@fortawesome/fontawesome-free/js/all'
import AppHeader from './components/AppHeader'
import TokenPicker from './components/TokenPicker'
import DexData from './components/DexData'
import AppStatusBar from './components/AppStatusBar'
import { Component } from 'react'

//import {web3Init} from './components/AppWeb3'

const Web3 = require('web3')

const PAGE = styled.div`
  margin: 0px 10px 0px 10px
`
const Pages = {
  HOME:'HOME',
  TRADE:'TRADE',
  CHARTS:'CHARTS',
  ORDER:'ORDERS'
}

function App(props) {

  /**
   * State brainstorm:
   * Pages: Trade, Charts, Order History
   * 
   */

  // const [page, setBalance] = useState(10000)
  // const [balanceHidden, setBalanceHidden] = useState(false)
  // const [coinData, setCoinData] = useState([])

  const [initReady, setInitReady] = useState(false)
  const [network, setNetwork] = useState({})
  const [contract, setContract] = useState({})

  const componentWillMount = async () => {
    await loadBlockchainData()
  }

  const loadBlockchainData = async () => {
    const web3 = new Web3("http://127.0.0.1:7545")
    const accounts = await web3.eth.getAccounts()
    const netId = await web3.eth.net.getId()

    const dex = require('./components/Dex.json')
    const abi = dex.abi;
    const address = dex.networks[netId].address
    setNetwork((network) => ({accounts, netId}))
    setContract((contract) => ({abi, address}))
  }

  useEffect(() => {
    if (!initReady) {
      setInitReady(true, componentWillMount)
      componentWillMount()
      console.log("Web3 init completed")
    }
  })

  return (
    <Router>
    <div className="App">
      <PAGE>
        <AppHeader title='DEX'/>
        <TokenPicker />
        <DexData />
        <AppStatusBar />
      </PAGE>
    </div>
    </Router>
  )
}

export default App;
