// Logo
// Page control
// Wallet connection

/** 
 * Design:The header must be transparent so that the background can be seen through.
 * 
 * */ 
import '../App.css'

import logo from '../logo.svg';
import styled from 'styled-components'
import React, { Component } from 'react'

const Img = styled.img`
  height: 4rem;
  pointer-events: none;

  @media (prefers-reduced-motion: no-preference) {
    animation: App-logo-spin infinite 20s linear;
  }

  @keyframes App-logo-spin {
    from {
      transform: rotate(0deg);
    }
    to {
      transform: rotate(360deg);
    }
  }
`

const Header = styled.header`
  min-height: 10vh;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
`

const WRAPPER = styled.div`
    width: 33%;
    height: inherit;
`
const WRAPPER2 = styled(WRAPPER)`
    text-align: left;
`

const LogoTitle = styled.div`
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: flex-start;
    color: white;
`

const H1 = styled.h1`
  font-size: 2rem;
  font-weight: bold;
`

const PageControl = styled.div`
    color: black
`

const Button = styled.button`
  width: 200px
`
const NavigationButton = styled.button`
    font-weight: bold;
    opacity: 60%;
`

const aDummyClick = async (e) => {
    console.log(e)
}

const dummyClick = (e) => {
    console.log(e);
}

export default class AppHeader extends Component {
    render() {

        // Changes based on the wallet connection state
        const btnWalletTitle = 'Connect Wallet'

        return (
            <Header>
                <WRAPPER>
                <LogoTitle>
                    <Img src={logo} alt="logo" />
                    <H1>
                        {this.props.title}
                    </H1>
                </LogoTitle>
                </WRAPPER>
                <WRAPPER>
                <PageControl className="btn-group" role="group">
                    <button type="button" id='btn-transparent' className="btn btn-dark">Trade</button>
                    <button type="button" id='btn-transparent' className="btn btn-dark">Charts</button>
                    <button type="button" id='btn-transparent' className="btn btn-dark">Orders</button>
                </PageControl>
                </WRAPPER>
                <WRAPPER2>
                <Button 
                    className='btn btn-warning' 
                    onClick={async (e) => {aDummyClick(e)}}>
                        {btnWalletTitle}
                </Button>
                </WRAPPER2>
            </Header>
        )
    }
}
