/**
 * 
 * Import web3 and dependencies
 * 
 * 
 * Connect to the development chain (Ganache chain @ 127.0.0.1:7545)
 * 
 * 
 * Read Dex contract abi and address
 */

const web3Init = () => {

    const Web3 = require('web3')
    const fs = require('fs')

    const web3 = new Web3('http://127.0.0.1:7545')
    const netId = web3.eth.net.getId()

    console.log(netId)

    const dexJson = JSON.parse(fs.readFileSync('../build/contracts/Dex.json'))
    const dexAbi = dexJson.abi
    const dexAddress = dexJson.networks[netId].address

    const dexContract = new web3.eth.Contract(dexAbi, dexAddress)

    console.log(dexContract)

}
