// Logo
// Page control
// Wallet connection

/** 
 * Design:The header must be transparent so that the background can be seen through.
 * 
 * */ 
import '../App.css'

import logo from '../logo.svg';
import styled from 'styled-components'
import React, { Component } from 'react'

const Img = styled.img`
  height: 4rem;
  pointer-events: none;

  @media (prefers-reduced-motion: no-preference) {
    animation: App-logo-spin infinite 20s linear;
  }

  @keyframes App-logo-spin {
    from {
      transform: rotate(0deg);
    }
    to {
      transform: rotate(360deg);
    }
  }
`

const Header = styled.header`
  min-height: 10vh;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
`

const WRAPPER = styled.div`
    width: 33%;
    height: inherit;
`
const WRAPPER2 = styled(WRAPPER)`
    width: 34%;
    text-align: right;
`

const LogoTitle = styled.div`
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: flex-start;
    color: white;
`

const H1 = styled.h1`
  font-size: 2rem;
  font-weight: bold;
`

const PageControl = styled.div`
    color: black
`

const Button = styled.button`
  width: 200px
`
const NavigationButton = styled.button`
    font-weight: bold;
    opacity: 60%;
`

const aDummyClick = async (e) => {
    console.log(e)
}

const dummyClick = (e) => {
    console.log(e);
}

export default function AppHeader(props) {
    // Changes based on the wallet connection state
    const btnWalletTitle = 'Connect Wallet'

    return (
        <Header>
            <WRAPPER>
            <LogoTitle>
                <Img src={logo} alt="logo" />
                <H1>
                    {props.title}
                </H1>
            </LogoTitle>
            </WRAPPER>
            <WRAPPER>
            <ul className="nav nav-pills">
            <li className="nav-item">
                <a className="nav-link active" href="#">Active</a>
            </li>
            <li className="nav-item dropdown">
                <a className="nav-link dropdown-toggle" data-bs-toggle="dropdown" href="#" role="button" aria-haspopup="true" aria-expanded="false">Dropdown</a>
                <div className="dropdown-menu">
                    <a className="dropdown-item" href="#">Action</a>
                    <a className="dropdown-item" href="#">Another action</a>
                    <a className="dropdown-item" href="#">Something else here</a>
                    <div className="dropdown-divider"></div>
                    <a className="dropdown-item" href="#">Separated link</a>
                </div>
            </li>
            <li className="nav-item">
                <a className="nav-link" href="#">Link</a>
            </li>
            <li className="nav-item">
                <a className="nav-link disabled" href="#">Disabled</a>
            </li>
            </ul>
            </WRAPPER>
            <WRAPPER2>
            <Button 
                className='btn btn-warning' 
                onClick={async (e) => {aDummyClick(e)}}>
                    {btnWalletTitle}
            </Button>
            </WRAPPER2>
        </Header>
    )
}

